import WebSocket from "./WebSocket";

function App() {
    const [count, setCount] = useState(0);
    const wsRef = useRef(null);

    const WebSocketForwarded = React.forwardRef(WebSocket);

    function handleData(data) {
        console.log("Message received: " + data);
        setCount(data);
    }

