        }
    }

    return (
        <div>
            Count: <strong>{count}</strong>
            <button onClick={() => sendMessage("Hello Server")}>Send Message</button>
            <WebSocketForwarded url='ws://localhost:5000/echo'
                                reconnect={true} debug={true}
                                ref={wsRef} onMessage={(data) => handleData(data)}/>
        </div>
    );
}


export default App;
